var $ = require('jquery');
var _ = require('lodash');
var Q = require('q');
var isBrowser = false;
var server = 'http://localhost:3333/';
var Moment = require('moment');
var isBrowser = typeof process === 'undefined';
var evernoteSync = require('tracker/sync/evernote');


exports.sync = function (config) {
    var mStart, mEnd;
    if (config.start && config.end) {
        mStart = new Moment(config.start);
        mEnd = new Moment(config.end);
    } else {
        mStart = new Moment().startOf('month');
        mEnd = new Moment().endOf('month');
    }
    if (config.type === 'evernote') {
        evernoteSync.sync({
            start: mStart.toDate(),
            end: mEnd.toDate()
        }).then(function (result) {
            alert(result);
        });
    }
    syncEvernote.sync({
        start: mStart.toDate(),
        end: mEnd.toDate()
    });
}