var mongoose = require('mongoose'),
    Schema = mongoose.Schema,
    ObjectId = Schema.ObjectId;

var Goal = Schema({
    name: String,
    estimatedTime: Number, //estimated time that complete goal would need
    granularity: String, // month/day/year/week
    filter: Object,
    start: Date,
    end: Date,
    halt: {type: Boolean, default: false}
});


module.exports = mongoose.model('Goal', Goal);
