var mongoose = require('mongoose'),
    Schema = mongoose.Schema,
    ObjectId = Schema.ObjectId;

var GoalAchievement = Schema({
    date: Date,
    goalId: ObjectId,
    time: Number,
    progress: Number,
    achieveTime: Date,
    updateTime: Date
});



module.exports = mongoose.model('GoalAchievement', GoalAchievement);
