/**
 * Task
 */
'use strict';

var numeral = require('numeral');

var Task = function (name, attrs) {
    var progress;
    this.name = name;
    if (attrs) {
        progress =  attrs.progress || attrs.pg;
        delete attrs.progress;
        delete attrs.pg;
    }
    this.attributes = attrs || {};
    if (progress) {
        this.progress = numeral(progress).value();
    } else {
        this.progress = 100;
    }
};

module.exports = Task;
