var mongoose = require('mongoose'),
    Schema = mongoose.Schema;

var Project = Schema({
    /**
     * 类型
     * Movie, Book, Music, Program
    type: String,
     */

    //分类名称
    name: String,

    //其他属性
    attributes: Object,

    //last active date
    lastActiveTime: Date,

    //created date
    createdTime: Date,

    //tags
    tags: Array,

    //classes
    classes: Array,

    //progress
    progress: {type: Number, default: 0}
});

module.exports = mongoose.model('Project', Project);
