var mongoose = require('mongoose'),
    Schema = mongoose.Schema,
    ObjectId = Schema.ObjectId;

var Task = Schema({
    name: String,
    attributes: Object,
    parent: {type: ObjectId, ref: "Task"},
    projectId: ObjectId,
    versionId: ObjectId,
    progress: {type: Number, default: 0},
    createTime: Date,
    lastActiveTime: Date
});

module.exports = mongoose.model('Task', Task);
