var mongoose = require('mongoose'),
    Schema = mongoose.Schema,
    ObjectId = Schema.ObjectId;

var Version = Schema({
    /**
     * 类型
     * Movie, Book, Music, Program
    type: String,
     */

    //分类名称
    name: String,

    //所属项目
    projectId: ObjectId,

    //其他属性
    attributes: Object,

    createTime: Date,

    lastActiveTime: Date,

    progress: {type: Number, default: -1},

    completeTime: Date, //the time when the version completed

    estimatedTime: Number //estimated time that version would need
});

module.exports = mongoose.model('Version', Version);
