/**
 * multiple days statist
 *
 * provide a landscape view of life in a period of time
 * for example
 *    total sleep time
 *    total work time
 * group time consume in different dimensions;
 */

'use strict';


var multipleDaysStatist = require('./multipleDays');
var helper = require('../helper');
exports.dispose = function(options, scanResult) {
    var months = helper.mergeDaysToMonths(scanResult.days);

    var monthsStatResult = months.map(function (month) {
        var result = multipleDaysStatist.dispose(options, month);
        delete result.days;
        return {
            month: month.month,
            data: result
        };
    });
    return {
        months: monthsStatResult
    };
};

