/**
 * sit perspective
 *
 * see how many time I sit in one period of time.
 */
'use strict';


exports.focus = function (options, data) {
    var days = data.days;
    var standTime = 0,
        sitTime = 0;
    days.forEach(function (day) {
        var sitData = day.sitPerspective;
        if (sitData) {
            sitTime += sitData.sitTime;
            standTime += sitData.standTime;
        }
    });
    return {
        standTime: standTime,
        sitTime: sitTime
    };
};
